(function(mod) {
  // console.log(mod);
  if (typeof exports == "object" && typeof module == "object") // CommonJS
    mod(require("../../lib/codemirror"));
  else if (typeof define == "function" && define.amd) // AMD
    define(["../../lib/codemirror"], mod);
  else // Plain browser env
    mod(CodeMirror);
})(function(CodeMirror) {
  var Pos = CodeMirror.Pos;
  // console.log(1223);
  function forEach(arr, f) {
    for (var i = 0, e = arr.length; i < e; ++i) f(arr[i]);
  }

  function arrayContains(arr, item) {
    if (!Array.prototype.indexOf) {
      var i = arr.length;
      while (i--) {
        if (arr[i] === item) {
          return true;
        }
      }
      return false;
    }
    return arr.indexOf(item) != -1;
  }

  function scriptHint(editor, keywords, getToken, options) {
    // console.log('开始scriptHint()方法，显示js语法的代码提示');
    // Find the token at the cursor
    // 在光标处找到标记
    // console.log('调用getToken()方法，获取Token');
    var cur = editor.getCursor(), token = getToken(editor, cur);
    if (/\b(?:string|comment)\b/.test(token.type)) return;
    // console.log('调用innerMode()方法，在codeMirror.js');
    var innerMode = CodeMirror.innerMode(editor.getMode(), token.state, 'javascript');
    // console.log(innerMode);
    if (innerMode.mode.helperType === "json") return;
    token.state = innerMode.state;

    // If it's not a 'word-style' token, ignore the token.
    // 如果它不是“word样式”标记，请忽略该标记。
    if (!/^[\w$_]*$/.test(token.string)) {
      // console.log('忽略');
      token = {start: cur.ch, end: cur.ch, string: "", state: token.state,
               type: token.string == "." ? "property" : null};
    } else if (token.end > cur.ch) {
      // console.log('不忽略');
      token.end = cur.ch;
      token.string = token.string.slice(0, cur.ch - token.start);
    }

    var tprop = token;
    // console.log(tprop.type);
    // If it is a property, find out what it is a property of.
    // 如果它是一个属性，找出它是什么属性。
    while (tprop.type == "property") {
      // console.log('是属性');
      tprop = getToken(editor, Pos(cur.line, tprop.start));
      if (tprop.string != ".") return;
      tprop = getToken(editor, Pos(cur.line, tprop.start));
      // console.log(tprop);
      if (!context) var context = [];
      context.push(tprop);
      // console.log(context);
    }
    // console.log(cur);
    // console.log(token);
    // console.log(context);
    // console.log(keywords);
    // console.log(options);
    const list = getCompletions(token, context, keywords, options);
    // list.push('ssdds')
    // console.log(list);
    return {list: list,
            from: Pos(cur.line, token.start),
            to: Pos(cur.line, token.end)};
  }

  function javascriptHint(editor, options) {
    // console.log('开始javascriptHint()方法，显示js语法的代码提示');
    // console.log('调用scriptHint()方法，显示js语法的代码提示');
    return scriptHint(editor, javascriptKeywords,
                      function (e, cur) {
                        return e.getTokenAt(cur, undefined, 'javascript');
                      },options);
  };
  CodeMirror.registerHelper("hint", "javascript", javascriptHint);

  function getCoffeeScriptToken(editor, cur) {
  // This getToken, it is for coffeescript, imitates the behavior of
  // getTokenAt method in javascript.js, that is, returning "property"
  // type and treat "." as independent token.
    var token = editor.getTokenAt(cur);
    if (cur.ch == token.start + 1 && token.string.charAt(0) == '.') {
      token.end = token.start;
      token.string = '.';
      token.type = "property";
    }
    else if (/^\.[\w$_]*$/.test(token.string)) {
      token.type = "property";
      token.start++;
      token.string = token.string.replace(/\./, '');
    }
    return token;
  }

  function coffeescriptHint(editor, options) {
    // console.log(coffeescriptKeywords);
    return scriptHint(editor, coffeescriptKeywords, getCoffeeScriptToken, options);
  }
  CodeMirror.registerHelper("hint", "coffeescript", coffeescriptHint);

  var stringProps = ("charAt charCodeAt indexOf lastIndexOf substring substr slice trim trimLeft trimRight " +
                     "toUpperCase toLowerCase split concat match replace search").split(" ");
  var arrayProps = ("length concat join splice push pop shift unshift slice reverse sort indexOf " +
                    "lastIndexOf every some filter forEach map reduce reduceRight ").split(" ");
  var funcProps = "prototype apply call bind".split(" ");
  var javascriptKeywords = ("break case catch class const continue debugger default delete do else export extends false finally for function " +
                  "if in import instanceof new null return super switch this throw true try typeof var void while with yield").split(" ");
  var coffeescriptKeywords = ("and break catch class continue delete do else extends false finally for " +
                  "if in instanceof isnt new no not null of off on or return switch then throw true try typeof until void while with yes").split(" ");

  function forAllProps(obj, callback) {
    // console.log(obj);
    // console.log(callback);
    // console.log(Object.getOwnPropertyNames);
    // console.log(Object.getPrototypeOf);
    if (!Object.getOwnPropertyNames || !Object.getPrototypeOf) {
      // console.log(1);
      for (var name in obj) callback(name)
    } else {
      // console.log(2);
      for (var o = obj; o; o = Object.getPrototypeOf(o)) {
        // console.log(o);
        // console.log(Object.getOwnPropertyNames(o));
        Object.getOwnPropertyNames(o).forEach(callback)
      }
    }
  }

  function getCompletions(token, context, keywords, options) {
    var found = [], start = token.string, global = options && options.globalScope || window;
    // console.log(global);
    function maybeAdd(str) {
      if (str.lastIndexOf(start, 0) == 0 && !arrayContains(found, str)) {
        // console.log(str);
        found.push(str);
      }
    }
    function gatherCompletions(obj) {
      // console.log(obj);
      // console.log(typeof obj);
      // console.log(obj instanceof Array);
      // console.log(obj instanceof Function);
      if (typeof obj == "string") forEach(stringProps, maybeAdd);
      else if (obj instanceof Array) forEach(arrayProps, maybeAdd);
      else if (obj instanceof Function) forEach(funcProps, maybeAdd);
      forAllProps(obj, maybeAdd)
    }
    // console.log(context);
    if (context && context.length) {
      // If this is a property, see if it belongs to some object we can
      // find in the current environment.
      // 如果这是一个属性，请查看它是否属于我们在当前环境中可以找到的某个对象。
      // console.log('找到属性对应的对象');
      var obj = context.pop(), base;
      // console.log(obj);
      // console.log(obj.type);
      if (obj.type && obj.type.indexOf("variable") === 0) {
        // console.log('obj的type为variable');
        // console.log(options);
        // console.log(options.additionalContext);
        // console.log(options.useGlobalScope);
        if (options && options.additionalContext) {
          // console.log(111);
          base = options.additionalContext[obj.string];
        }
        if (!options || options.useGlobalScope !== false) {
          // console.log(222);
          base = base || global[obj.string];
        }
        // console.log(base);
      } else if (obj.type == "string") {
        base = "";
      } else if (obj.type == "atom") {
        base = 1;
      } else if (obj.type == "function") {
        if (global.jQuery != null && (obj.string == '$' || obj.string == 'jQuery') &&
            (typeof global.jQuery == 'function'))
          base = global.jQuery();
        else if (global._ != null && (obj.string == '_') && (typeof global._ == 'function'))
          base = global._();
      }
      while (base != null && context.length)
        base = base[context.pop().string];
      if (base != null) gatherCompletions(base);
    } else {
      // If not, just look in the global object, any local scope, and optional additional-context
      // (reading into JS mode internals to get at the local and global variables)
      // 如果没有，只需查看全局对象、任何局部范围和可选的附加上下文（读入JS模式内部以获取局部和全局变量）
      // console.log('不是属性');
      if (token.state) {
        for (var v = token.state.localVars; v; v = v.next) maybeAdd(v.name);
        for (var c = token.state.context; c; c = c.prev)
          for (var v = c.vars; v; v = v.next) maybeAdd(v.name)
        for (var v = token.state.globalVars; v; v = v.next) maybeAdd(v.name);
      }
      if (options && options.additionalContext != null)
        for (var key in options.additionalContext)
          maybeAdd(key);
      if (!options || options.useGlobalScope !== false)
        gatherCompletions(global);
      forEach(keywords, maybeAdd);
    }
    return found;
  }
});
